import { useState } from "react";
import ExpenseItem from "./ExpenseItem";
import "./Expenses.css";
import Card from "../UI/Card.js";
import ExpensesFilter from "./ExpensesFilter.js";
const Expenses= (props)=> {
  const [filteredYear,setFilterdYear] = useState('2020');
  const [filterInfo,setFilterInfo] = useState('2019,2021 & 2022')
  const filterChangeHandler = (selctedYear)=>{
    setFilterdYear(selctedYear);
    
    if (selctedYear==='2019'){
      setFilterInfo('2020,2021,2022')
    }else if (selctedYear==='2020'){
      setFilterInfo('2019,2021,2022')
    }else if (selctedYear==='2021'){
      setFilterInfo('2019,2020,2022')
    }else{
      setFilterInfo('2019,2020,2021')
    }
    };


  return (
    <div>
    <Card className="expense">
    <ExpensesFilter selected={filteredYear} onChangeFilter={filterChangeHandler}/>
    <p>Data of the Year {filterInfo} is hidden</p>
      <ExpenseItem
        title={props.items[0].title}
        amount={props.items[0].amount}
        date={props.items[0].date}
      ></ExpenseItem>
      <ExpenseItem
        title={props.items[1].title}
        amount={props.items[1].amount}
        date={props.items[1].date}
      ></ExpenseItem>
      <ExpenseItem
        title={props.items[2].title}
        amount={props.items[2].amount}
        date={props.items[2].date}
      ></ExpenseItem>
      <ExpenseItem
        title={props.items[3].title}
        amount={props.items[3].amount}
        date={props.items[3].date}
      ></ExpenseItem>
    </Card>
    </div>
  );
}
export default Expenses;
