import { useState } from "react";
import React from "react";
import "./NewExpenses.css";
const NewExpenses = () => {
  const [entredTitle, setEntredTitle] = useState('');
  const [entredAmount,setEntredAmount] = useState('');
  const [entreDate,setEntredDate] = useState('');

  const titleHandle = (event) => {
    setEntredTitle(event.target.value);
  };
  const amountHandler = (event) =>{
    setEntredAmount(event.target.value);
  }
  const dateChangeHandler = (event) => {
    setEntredDate(event.target.value)
  }

  const submitHandler = (event) => {
    event.preventDefault();

    const expenseData={
      title: entredTitle,
      amount: entredAmount,
      date: new Date(entreDate)
    };
    
    setEntredTitle('');
    setEntredAmount('');
    setEntredDate('');
  };


  return (
    <div className="new-expense" >
      <form onSubmit={submitHandler}>
        <div className="new-expense__controls">
          <div classNamenew-expense__control>
            <label>title</label>
            <input type="text" value={entredTitle} onChange={titleHandle} />
          </div>
          <div classNamenew-expense__control>
            <label>amount</label>
            <input type="number" min="0.01" step="0.01" value={entredAmount} onChange={amountHandler}/>
          </div>
          <div classNamenew-expense__control>
            <label>Date</label>
            <input type="date" min="2019-01-01" max="2022-12-31" value={entreDate} onChange={dateChangeHandler}  />
          </div>
        </div>
        <div className="new-expense__action">
          <button type="submit">Add Expense</button>
        </div>
      </form>
    </div>
  );
};
export default NewExpenses;
